import React, { PureComponent } from 'react'
import { useNavigate } from 'react-router-dom';

class SearchBoxClass extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {

        }
    }

    handleSearch = () => {
        this.props.navigate('/home');
    }

    render() {
        return (
            <div>
                <div className="search-box">
                    <div className="search-box__container">
                        <div className="search-box__text">
                            <h4>Find your perfect trip, designed by insiders who know and love their cities.</h4>
                        </div>
                        <div className="search-box__input">
                            <input type="text" placeholder="Search popular cities" />
                        </div>
                        <div className="search-box__input">
                            <input type="date" placeholder="Check-in date" />
                        </div>
                        <div className="search-box__input">
                            <input type="date" placeholder="Check-out date" />
                        </div>
                        <div className="search-box__button">
                            <button type='button' onClick={this.handleSearch}>Search
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const SearchBox = () => {
    const navigate = useNavigate();
    return <SearchBoxClass navigate={navigate} />
}

export default SearchBox;
