import React, { PureComponent } from 'react'
import './HotelPage.css'

class HotelPage extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            hotel: null
        }
    }

    componentDidMount() {
        const hotelId = this.props.location.state;

        // Fetch the hotel information and amenities
        fetch(`http://localhost:8080/api/hotels/${hotelId}`)
            .then(response => response.json())
            .then(data => this.setState({ hotel: data }))
            .catch(error => console.error('Error:', error));
    }

    render() {
        return (
            <div>
                <h1>Hotel Page</h1>
                {this.state.hotel && (
                    <div>
                        <h2>{this.state.hotel.hotelName}</h2>
                        <p>{this.state.hotel.hotelInformation}</p>
                        {this.state.hotel.amenities.map((amenity, index) => (
                            <p key={index}>{amenity}</p>
                        ))}
                    </div>
                )}
            </div>
        )
    }
}

export default HotelPage
