import React, { Component, useContext } from 'react'
import './LoginUser.css'
import { useInRouterContext, useNavigate } from 'react-router-dom';


class LoginUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
      username: '',
      password: ''
    }
  }

  handleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    // handle login logic here

    // After login logic, redirect to the starting page
    this.props.setUsername(this.state.username);
    this.props.navigate('/');
  }

  render() {
    return (
      <div className="login-container">
        <h2>Login</h2>
        <form className="login-form" onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label>Username:</label>
            <input type="text" name="username" onChange={this.handleInputChange} />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input type="password" name="password" onChange={this.handleInputChange} />
          </div>
          <input className="submit-button" type="submit" value="Submit" />
        </form>
      </div>
    )
  }
}

const LoginUserWithNavigate = () => {
  const navigate = useNavigate();
  const { setUsername } = useContext(useInRouterContext);
  return <LoginUser navigate={navigate} setUsername={setUsername} />;
}

export default LoginUserWithNavigate;
