import React, { PureComponent } from 'react'
// import { useNavigate } from 'react-router-dom';

import { useNavigate, useLocation } from 'react-router-dom';

function useRouter() {
    const params = useParams();
    const location = useLocation();
    const navigate = useNavigate();
    const match = useRouteMatch();

    // Mimic the old `history` object
    const history = {
        push: navigate,
        location,
        match,
        params,
    };

    return history;
}

class SearchBox extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {

        }
    }

    handleClick = () => {
        this.props.history.push("/target");
    }

    render() {
        return (
            <div>
                <div className="search-box">
                    <div className="search-box__container">
                        <div className="search-box__text">
                            <h4>Find your perfect trip, designed by insiders who know and love their cities.</h4>
                        </div>
                        <div className="search-box__input">
                            <input type="text" placeholder="Search popular cities" />
                        </div>
                        <div className="search-box__input">
                            <input type="date" placeholder="Check-in date" />
                        </div>
                        <div className="search-box__input">
                            <input type="date" placeholder="Check-out date" />
                        </div>
                        <div className="search-box__button">

                            <button type='button'>Search</button>

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default SearchBox;
