import React, { Component } from 'react'
import './NavBar.css'
class NavBar extends Component {
    state = {
        isOpen: false
    }

    toggleMenu = () => {
        this.setState(prevState => ({ isOpen: !prevState.isOpen }));
    }

    render() {
        const { isOpen } = this.state;
        return (
            <nav className={`navbar ${isOpen ? 'open' : ''}`}>
                <h1 className="navbar-heading">SheShare.com</h1>
                <div className="button-group">
                    <button className='b1' >Login</button>
                    <button className='b1'>Register</button>
                </div>
                <div className="wrapper">
                    <nav className="navbar1">
                        <div onClick={this.toggleMenu} className={`hamburger-menu ${isOpen ? 'open' : ''}`}>
                            <div id="line1" className="hamburger-line"></div>
                            <div id="line2" className="hamburger-line"></div>
                            <div id="line3" className="hamburger-line"></div>
                        </div>
                    </nav>

                    <div id="menu" className={`menu ${isOpen ? 'open' : ''}`}>
                        <ul>
                            <li className="menu-item" onClick={this.toggleMenu}>Home</li>
                            <li className="menu-item" onClick={this.toggleMenu}>About</li>
                            <li className="menu-item" onClick={this.toggleMenu}>Contact Us</li>
                            <li className="menu-item" onClick={this.toggleMenu}>Login</li>
                        </ul>
                    </div>
                </div>
            </nav>
        )
    }
}

export default NavBar
