import { PureComponent } from 'react';
import { useNavigate } from 'react-router-dom';
im

class ProfileNameClass extends PureComponent {
    handleClick = () => {
        this.props.navigate("/hobbies-interests");
    }

    render() {
        return (
            <div className="container">
                <label>
                    Name:
                    <input type="text" name="name" placeholder="Enter your name" onChange={this.handleInputChange} />
                </label>
                <label>
                    Age:
                    <input type="number" name="age" placeholder="Enter your age" onChange={this.handleInputChange} />
                </label>
                <button type="button" onClick={this.handleClick}>
                    Next Step
                </button>
            </div>
        )
    }
}

const ProfileName = () => {
    const navigate = useNavigate();
    return <ProfileNameClass navigate={navigate} />
}

export default ProfileName;
