import React, { Component, useContext} from 'react';
import './LoginUser.css'
import { useNavigate } from 'react-router-dom';
import { UsernameContext } from '../helpers/UsernameContext';

class LoginUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
      username: '',
      password: ''
    }
  }

  handleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  handleSubmit = async (event) => {
    event.preventDefault();

    // Determine the correct endpoint based on the URL
    let endpoint;
    if (this.props.userType === 'seller') {
      endpoint = 'http://localhost:8080/api/hotels/login/seller';
    } else {
      endpoint = 'http://localhost:8080/api/hotelslogin/buyer';
    }

    // Send a request to the login endpoint
    const response = await fetch(endpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: this.state.username,
        password: this.state.password,
      }),
    });

    if (response.ok) {
      // If the request was successful, navigate to the appropriate page
      this.props.setUsername(this.state.username);
      if (this.props.userType === 'seller') {
        this.props.navigate('/seller');
      } else {
        this.props.navigate('/home');
      }
    } else {
      // If the request was not successful, show an error message
      alert('Invalid username or password');
    }
  }

  render() {
    return (
      <UsernameContext.Consumer>
      {({ setUsername }) => (
        <div className="login-container">
          <h2>Login</h2>
          <form className="login-form" onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Username:</label>
              <input type="text" name="username" onChange={this.handleInputChange} />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input type="password" name="password" onChange={this.handleInputChange} />
            </div>
            <input className="submit-button" type="submit" value="Submit" />
          </form>
        </div>
      )}
      </UsernameContext.Consumer>
    )
  }
}

const LoginUserWithNavigate = ({ userType }) => {
  const navigate = useNavigate();
  const { setUsername } = useContext(UsernameContext);
  return <LoginUser navigate={navigate} setUsername={setUsername} userType={userType} />;
}

export default LoginUserWithNavigate;
