import React, { Component } from 'react';
import './SearchBox.css';

class SearchBox extends Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            checkInDate: '',
            checkOutDate: '',
            roomOption: ''
        };
    }

    handleInputChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();
        if (!this.state.location || !this.state.checkInDate || !this.state.checkOutDate || !this.state.roomOption) {
            alert('Please fill all the options.');
            return;
        }
        // Fetch the data based on the state and update the state of the parent component
    }


    render() {
        return (
            <form className="search-box" onSubmit={this.handleSubmit}>
                <input type="text" name="location" placeholder="Where" onChange={this.handleInputChange} />
                <div className="date-inputs">
                    <input type="date" name="checkInDate" onChange={this.handleInputChange} />
                    <input type="date" name="checkOutDate" onChange={this.handleInputChange} />
                </div>
                <input type="submit" value="Search" />
            </form>

        );
    }
}

export default SearchBox;
