import React, { PureComponent } from 'react'
import { useLocation } from 'react-router-dom';

class HomePage extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {
            hotels: []
        }
    }

    componentDidMount() {
        const locationState = this.props.location.state;
        if (locationState) {
            console.log("HomePage state:", locationState); 
            this.setState({
                location: locationState.location,
                checkInDate: locationState.checkInDate,
                checkOutDate: locationState.checkOutDate
            });
        }
    }

    render() {
        return (
            <div>
                <h1>Home Page</h1>
                <p>Location: {this.state.location}</p>
                <p>Check-in Date: {this.state.checkInDate}</p>
                <p>Check-out Date: {this.state.checkOutDate}</p>
            </div>
        )
    }
}

const HomePageWithLocation = () => {
    const location = useLocation();
    return <HomePage location={location} />
}

export default HomePageWithLocation;
