import React, { Component, useContext} from 'react';
import './LoginUser.css'
import { useNavigate } from 'react-router-dom';
import { UsernameContext } from '../helpers/UsernameContext';


class LoginUser extends Component {
  constructor(props) {
    super(props)

    this.state = {
      username: '',
      password: ''
    }
  }

  handleSubmit = async (event) => {
    event.preventDefault();
  
    // Determine the correct endpoint and field names based on the URL
    let endpoint, emailField, passwordField, navigateTo;
    if (this.props.userType === 'seller') {
      endpoint = 'http://localhost:8080/api/hotels/login/seller';
      emailField = 'sellerEmail';
      passwordField = 'sellerPassword';
      navigateTo = '/seller';
    } else {
      endpoint = 'http://localhost:8080/api/hotels/login/buyer';
      emailField = 'buyerEmail';
      passwordField = 'buyerPassword';
      navigateTo = '/home';
    }
  
    // Send a request to the login endpoint
    const response = await fetch(endpoint, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        [emailField]: this.state.username,
        [passwordField]: this.state.password,
      }),
    });
  
    if (response.ok) {
      // If the request was successful, navigate to the appropriate page
      this.props.setUsername(this.state.username);
      this.props.navigate(navigateTo);
    } else {
      // If the request was not successful, show an error message
      alert('Invalid username or password');
    }
  }

}

const LoginUserWithNavigate = () => {
  const navigate = useNavigate();
  const { setUsername } = useContext(UsernameContext);
  return <LoginUser navigate={navigate} setUsername={setUsername} />;
}

export default LoginUserWithNavigate;
