import React, { PureComponent } from 'react'
import { useNavigate } from 'react-router-dom';

class SearchBoxClass extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {
            location: '',
            checkInDate: '',
            checkOutDate: ''
        }
    }

    
    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });

    }

    handleClick = async () => {
        const { location, checkInDate, checkOutDate } = this.state;
        

        const response = await fetch(`http://localhost:8080/api/hotels/search?location=${location}&checkInDate=${checkInDate}&checkOutDate=${checkOutDate}`);
        const data = await response.json();
        this.props.navigate("/home", { state: data });
    }

    render() {
        return (
            <div>
                <div className="search-box">
                    <div className="search-box__container">
                        <div className="search-box__text">
                            <h4>Find your perfect trip, designed by insiders who know and love their cities.</h4>
                        </div>
                        <div className="search-box__input">
                            <input type="text" name="location" placeholder="Search popular cities" onChange={this.handleChange} />
                        </div>
                        <div className="search-box__input">
                            <input type="date" name="checkInDate" placeholder="Check-in date" onChange={this.handleChange} />
                        </div>
                        <div className="search-box__input">
                            <input type="date" name="checkOutDate" placeholder="Check-out date" onChange={this.handleChange} />
                        </div>
                        <button type="button" onClick={this.handleClick}>
                            Search
                        </button>
                    </div>
                </div>
            </div>
        )
    }
}

const SearchBox = () => {
    const navigate = useNavigate();
    return <SearchBoxClass navigate={navigate} />
}

export default SearchBox;
