// This file contains the main App component which is the root component of the application. \
//It contains the routing logic for the application. 
//It also contains the state for the username which is used to store the username of the user. The setUsername method is used to set the username in the state. The UsernameContext.Provider is used to provide the username and setUsername to the child components. The Router component is used to provide routing functionality to the application. The Routes component is used to define the routes for the application. The Route component is used to define the individual routes. The NavBarWithNavigate component is used to display the navigation bar. The SearchBox component is used to display the search box. The PopularDestinations component is used to display the popular destinations. The RoomOptions component is used to display the room options. The HomePage component is used to display the home page. The Profile component is used to display the profile. The HobbiesInterest component is used to display the hobbies and interests. The ProfileName component is used to display the profile name. The HotelPage component is used to display the hotel details. The LoginUser component is used to display the login page. The SellerPage component is used to display the seller page. The Properties component is used to display the properties page.

import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import NavBarWithNavigate from './components/NavBar';
import SearchBox from './components/SearchBox';
import PopularDestinations from './components/PopularDestinations';
import RoomOptions from './components/RoomOptions';
import HomePage from './pages/HomePage';
import Profile from './components/Profile';
import HobbiesInterest from './pages/HobbiesInterest';
import ProfileName from './pages/ProfileName';
import HotelPage from './pages/HotelPage';
import LoginUser from './pages/LoginUser';
import { UsernameContext } from './helpers/UsernameContext';
import SellerPage from './pages/SellerPage';
import Properties from './pages/Properties';

class App extends Component {
  state = {
    username: null,
  };

  setUsername = (username) => {
    this.setState({ username });
  };
  render() {
    return (
      <UsernameContext.Provider value={{ username: this.state.username, setUsername: this.setUsername }}>
        <Router>
        <NavBarWithNavigate />
          <Routes>
            <Route path="/" element={
              <>
                <div className="main-content">
                  <RoomOptions />
                  <Profile />
                  <SearchBox />
                </div>
                <PopularDestinations />
              </>
            } />
            <Route path="/home" element={<HomePage />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/profile-name" element={<ProfileName />} />
            <Route path="/hobbies-interests" element={<HobbiesInterest />} />
            <Route path="/hotel-details" element={<HotelPage />} />
            <Route path="/login" element={<LoginUser />} />
            <Route path="/seller" element={<SellerPage/>} />
            <Route path="/properties" element={<Properties/>} />
            <Route path="*" element={<h1>Not Found</h1>} />
          </Routes>
        </Router>
      </UsernameContext.Provider>
    );
  }
}

export default App;
