import React, { Component } from 'react';
import './SearchBox.css';

class SearchBox extends Component {
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            checkInDate: '',
            checkOutDate: '',
            roomOption: ''
        };
    }

    handleInputChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();

        // Convert the check-in and check-out dates to the dd-MM-yyyy format
        const checkInDate = this.convertDate(this.state.checkInDate);
        const checkOutDate = this.convertDate(this.state.checkOutDate);

        // Construct the API URL with the search parameters
        const apiUrl = `http://localhost:8080/api/hotels/search?location=${this.state.location}&checkInDate=${this.state.checkInDate}&checkOutDate=${this.state.checkOutDate}&roomOption=${this.state.roomOption}`;

        // Fetch the properties from the API
        fetch(apiUrl)
            .then(response => response.json())
            .then(data => {
                console.log('Success:', data);
                // Update the state of the HomePage component with the response
                this.props.updateProperties(data);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }


    render() {
        return (
            <form className="search-box" onSubmit={this.handleSubmit}>
                <input type="text" name="location" placeholder="Where" onChange={this.handleInputChange} />
                <div className="date-inputs">
                    <input type="date" name="checkInDate" onChange={this.handleInputChange} />
                    <input type="date" name="checkOutDate" onChange={this.handleInputChange} />
                </div>
                <input type="submit" value="Search" />
            </form>

        );
    }
}

export default SearchBox;
