import React, { Component } from 'react'
import './NavBar.css'

class NavBar extends Component {
    // constructor(props) {
    //     super(props)

    //     this.state = {

    //     }
    // }

    isOpen = false;

    toggleMenu = () => {
        this.isOpen = !this.isOpen;
        const menu = document.getElementById("menu");
        menu.classList.toggle("open");

        const line1 = document.getElementById("line1");
        const line2 = document.getElementById("line2");
        const line3 = document.getElementById("line3");

        if (this.isOpen) {
            line1.style.transform = "rotate(45deg) translate(0, 0.5rem)";
            line2.style.opacity = "0";
            line2.style.transform = "translate(10px, 0)";
            line3.style.transform = "rotate(-45deg) translate(.43rem ,-1.12rem)";
        } else {
            line1.style.transform = "none";
            line2.style.opacity = "1";
            line2.style.transform = "none";
            line3.style.transform = "none";
        }
    }



    render() {
        return (
            <nav className="navbar">
                

                <h1 className="navbar-heading">SheShare.com</h1>
                <div className="button-group">
                    <button className='b1' >Login</button>
                    <button className='b1'>Register</button>

                </div>
                
            </nav>
        )
    }
}

export default NavBar
