import React from 'react';
import './SearchBox.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { connect } from 'react-redux';
import { setData } from '';

const SearchBox = () => {
    const [location, setLocation] = useState('');
    const [checkInDate, setCheckInDate] = useState('');
    const [checkOutDate, setCheckOutDate] = useState('');
    const [roomOption, setRoomOption] = useState('');
    const navigate = useNavigate();

    const handleInputChange = (event) => {
        switch (event.target.name) {
            case 'location':
                setLocation(event.target.value);
                break;
            case 'checkInDate':
                setCheckInDate(event.target.value);
                break;
            case 'checkOutDate':
                setCheckOutDate(event.target.value);
                break;
            case 'roomOption':
                setRoomOption(event.target.value);
                break;
            default:
                break;
        }
    }

    const convertDate = (dateStr) => {
        const date = new Date(dateStr);
        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');  // Months are 0-based
        const year = date.getFullYear();
        return `${day}-${month}-${year}`;
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        navigate('/home');

        // Convert the check-in and check-out dates to the dd-MM-yyyy format
        const checkIn = convertDate(checkInDate);
        const checkOut = convertDate(checkOutDate);

        // Construct the API URL with the search parameters
        const apiUrl = `http://localhost:8080/api/hotels/search?location=${location}&checkInDate=${checkIn}&checkOutDate=${checkOut}&roomOption=${roomOption}`;

        // Fetch the properties from the API
        fetch(apiUrl)
            .then(response => response.json())
            .then(data => {
                console.log('Success:', data);
                // Navigate to HomePage with the search results as state
                navigate('/home', { state: { data } });
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }

    return (
        <form className="search-box" onSubmit={handleSubmit}>
            <input type="text" name="location" placeholder="Where" onChange={handleInputChange} />
            <div className="date-inputs">
                <input type="date" name="checkInDate" onChange={handleInputChange} />
                <input type="date" name="checkOutDate" onChange={handleInputChange} />
            </div>
            <input type="submit" value="Search" />
        </form>
    );
}

export default SearchBox;
