import React, { PureComponent } from 'react'
// import { useNavigate } from 'react-router-dom';

import { useNavigate, useLocation } from 'react-router-dom';

function useRouter() {
    const location = useLocation();
    const navigate = useNavigate();

    // Mimic the old `history` object
    const history = {
        push: navigate,
        location,
    };

    return history;
}

class SearchBox extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {

        }
    }

    handleClick = () => {
        this.props.history.push("/HomePage");
    }

    render() {
        return (
            <div>
                <div className="search-box">
                    <div className="search-box__container">
                        <div className="search-box__text">
                            <h4>Find your perfect trip, designed by insiders who know and love their cities.</h4>
                        </div>
                        <div className="search-box__input">
                            <input type="text" placeholder="Search popular cities" />
                        </div>
                        <div className="search-box__input">
                            <input type="date" placeholder="Check-in date" />
                        </div>
                        <div className="search-box__input">
                            <input type="date" placeholder="Check-out date" />
                        </div>
                        {/* <div className="search-box__button">
                            <button type='button'>Search</button>
                        </div> */}
                        <button type="button" onClick={this.handleClick}>
                            Go to Target Page
                        </button>
                    </div>
                </div>
            </div>
        )
    }
}
function YourComponentWithRouter() {
    const history = useRouter();
  
    return <YourComponent history={history} />;
  }

export default SearchBox;
